/* * TCSS 342 */package structures;import exceptions.EmptyCollectionException;/** * A singly-linked implementation of the QueueADT. * * This Queue implementation allows null elements. *  * @author Alan Fowler - An adaptation of code from several textbooks * @version 1.2 *  * @param <T> the generic data type */public class LinkedQueue<T> implements QueueADT<T> {        /**     * EmptyCollectionException message.     */    private static final String MESSAGE = "queue";        /**     * The number of elements contained in the queue.     */    private int mySize;        /**     * A reference to the first node in the queue.     */    private LinearNode<T> myFront;        /**     * A reference to the last node in the queue.     */    private LinearNode<T> myRear;    /**     * Initialize an empty queue.     */    public LinkedQueue() {        mySize = 0;        myFront = null;        myRear = null;    }    @Override    public void enqueue(final T theElement) {        final LinearNode<T> node = new LinearNode<T>(theElement);        if (mySize == 0) {            myFront = node;        } else {            myRear.setNext(node);        }        myRear = node;        mySize++;    }    @Override    public T dequeue() throws EmptyCollectionException {        if (mySize == 0) {            throw new EmptyCollectionException(MESSAGE);        }        final T result = myFront.getElement();        myFront = myFront.getNext();        mySize--;        if (mySize == 0) {            myRear = null;        }        return result;    }    @Override    public T first() throws EmptyCollectionException {        if (isEmpty()) {            throw new EmptyCollectionException(MESSAGE);        }        return myFront.getElement();    }    @Override    public boolean isEmpty() {        return mySize == 0;    }    @Override    public int size() {        return mySize;    }    /**     * The returned String includes a comma separated listing of each element in the queue     * and includes a label for the front of the queue.     * This method will return an empty String if called on an empty queue.     *      * <p>For queue that is not empty the format of the returned String is:     * Front -> 8, 6, 7, 5, 3, 0, 9     */    @Override    public String toString() {        if (isEmpty()) {             return "";        }        LinearNode<T> current = myFront;        final StringBuilder sb = new StringBuilder();        sb.append("Front -> " + current.getElement().toString());        while (current.getNext() != null) {            current = current.getNext();            sb.append(", " + current.getElement().toString());        }                return sb.toString();    }}